import React from 'react';
import './modalWindowResultLearn.css'
function ModalWindowResultLearn(props) {
    function resultAnswers() {
        return props.answers.map(item => {
            return (<p> Number: {item.numberQuestion} | answers: {item.isRightAnswer} word: {item.word.word} translation: {item.word.translation} user word: {item.userWord}</p>)
        });
    }
    function result() {
        let countRightAnswers = 0;
        console.log(props.answers)
        props.answers.forEach(item => {
            if (item.isRightAnswer){
                countRightAnswers++
            }
        });
        return (<p>{countRightAnswers}/{props.answers.length}</p>)
    }
    return (
        <div className='modal-result'>
            {result()}
        </div>
    );
}

export default ModalWindowResultLearn;
