import React,{useState}  from 'react';
import axios from 'axios'


function AddWord(props) {
    let word = useState({
        word: '',
        translation: ''
    });
    async function addWordHandler(event) {
        event.preventDefault();
        axios.post('https://learn-words-7ba20-default-rtdb.firebaseio.com/words', word )
    }
    function addHeandler(){
        console.log(word[0])
    }
    return (
        <div>
            <input onChange={(event) => { word[0].word = event.target.value}} type="text"/>
            <input onChange={(event) => { word[0].translation = event.target.value}}  type="text"/>
            <button onClick={addWordHandler}>send</button>
        </div>
    );
}

export default AddWord;
