import React, {Component} from 'react';
import {GetAllThemesFromDB, GetAllWordsFromDB} from "../../components/DataDB/DataDB";
import ModalWindowThemes from "./modalWindowThemes/modalWindowThemes";


class ThemesList extends Component {
    state = {
        words: []
    };
    addThemeHandler() {
        let result =  GetAllWordsFromDB();
        this.setState(words = result);
    }
    componentDidMount() {

    }

    render() {
        return (
            <div className = 'body'>
                <button onClick = {this.addThemeHandler}>cheate new them</button>
                {!!(words[0]) ? <ModalWindowThemes />: <h1>123</h1>}
            </div>
        );
    }
}

export default ThemesList;
