import React,{useState}  from 'react';
import axios from 'axios'
import './addWord.css'

function AddWord(props) {
    let [wordEn, setWordEn] = useState('');
    let [wordRu, setWordRu] = useState('');
    let [validation, setValidation] = useState(false);
    let [errorRu, setErrorRu] = useState('');
    let [errorEn, setErrorEn] = useState('');
    async function addWordHandler(event) {
        console.log(errorRu, errorEn);
        event.preventDefault();
        validationForm();
        if (validation){
            try {
                await axios.post('https://react-words-29c77-default-rtdb.firebaseio.com/word.json', {word: wordEn,  });
            } catch (e) {
                console.log(e)
            }
        }
    }
    function validationForm() {
        errorEn = validationWord(word[0].translation);
        errorRu = validationWord(word[0].word);
        validation =  errorRu !== '' && errorEn !== ''
    }
    function validationWord(word) {
        if(word.trim().length === 0){
            return 'Entry field doesn\'t can be empty or complit of spaces'
        }
    }
    return (
        <div className = 'body'>
            <p>Русское слово</p>
            <input onChange={(event) => {word[0].word = event.target.value}} type="text"/>
            {validation ?<p className='exception_valid'>{errorRu}</p>: '1' }
            <p>Англиское слово</p>
            <input onChange={(event) => {word[0].translation = event.target.value}} type="text"/>
            {validation ?<p className='exception_valid'>{errorEn}</p>: '2' }
            <button onClick={addWordHandler}>send</button>
        </div>
    );
}
export default AddWord
