import axios from "axios";
export async function GetWordsFromDB(table) {
    try {
        let response = await axios.get(`https://learn-words-7ba20-default-rtdb.firebaseio.com/${table}.json`);
        let arrayWord = [];
        Object.keys(response.data).forEach((key, index) => {
            arrayWord.push({
                id: index,
                word:response.data[key].word,
                translation:response.data[key].translation,
            })
        });
        return await arrayWord
    }catch (e) {
        console.log(e)
    }
}
export async function sendWordsInDB(table) {
    try {
        await axios.post(`https://learn-words-7ba20-default-rtdb.firebaseio.com/${table}.json`, this.state.themesWords);
    }catch (e) {
        console.log(e)
    }
}
