import React from 'react';
import './modalWindowChooseTheme.css'
function ModalWindowChooseTheme(props) {
    function renderThemes() {
        return props.themes.map(item =>{
            return(
                <div onClick = {event => {event.currentTarget.classList.toggle('active-string');
                                          findeCurrentTheme(event.currentTarget.innerHTML)}} >
                    <p>{item.name}</p>
                </div>
            )
        })
    }
    function findeCurrentTheme(wordOfTheme) {
        props.themes.forEach(item =>{
            if(item.name = wordOfTheme){
                return item
            }
        })
    }
    return (
        <div id="block">
            <nav className="changeTheme">
                {renderThemes() }
                <button onClick={() => props.changeTheme('jjj')}>Выбрать</button>
            </nav>
        </div>
    );
}

export default ModalWindowChooseTheme;
