import React,{useState} from 'react';
import './modalWindowChooseTheme.css'
function ModalWindowChooseTheme(props) {
    const [isActive, setActive] = useState(false);
    function hiddenOrOpenWindow(event){
      console.log(event.target)
    }
    function renderTheme() {
        return props.themes.map(item =>{
            let listWords = item.list.map(word => {
                return(
                    <li ><a >{word.word}</a></li>
                )
            });
            if (isActive) {
                return(
                    <li><a className="active">{item.name} <button onClick={(event) => hiddenOrOpenWindow(event)}>скрыть</button> </a>
                        <ul className="submenu">
                            {listWords}
                        </ul>
                    </li>
                )
            }
        })
    }
    return (
        <div id="block">
            <nav>
                <ul className="topmenu">
                    {renderTheme() }
                </ul>
            </nav>
        </div>
    );
}

export default ModalWindowChooseTheme;
