import React, {Component} from 'react';
import GetAllWord from "../../../components/DataDB/DataDB";
import Word from "../../wordList/Word/Word";
import './modalWindow.css';
class ModalWindow extends Component {
    state = {
        words: []
    };
    componentDidMount() {
        GetAllWord().then(value => {
                this.setState({
                    words: value
                })
            }
        )
    }
    addWordsHandler(event){
       console.log(event.currentTarget.value)
    }
    onClickHandler(event){
        event.currentTarget.classList.toggle('active-string');
        console.log(event.target)
    }
    renderModalWindow(){
        return this.state.words.map(item =>{
            return  <div onClick = {this.onClickHandler} className = 'modal-string' >
                <p>{item.word}</p>
                <p>{item.translation}</p>
            </div>
        })
    }

    render() {
        return (
            <div className = 'ModalWindow'>
                {this.renderModalWindow()}
                <button onClick={this.addWordsHandler}>add</button>
            </div>
        );
    }
}

export default ModalWindow;
