import React, {Component} from 'react';
import Word from "./Word/Word";
import GetAllWord from "../../components/DataDB/DataDB";


class WordList extends Component {
    state = {
        arrayWord: []
    };
     componentDidMount() {
         GetAllWord().then(value => {
             this.setState({
                 arrayWord: value
             });
         })
    }

    renderWord(){
         console.log(this.state)
        return this.state.arrayWord.map(item => {
            return(
                <Word
                    key = {item.id}
                    word = {item.word}
                    translation = {item.translation}
                />
            )
        })
    }
    render() {
        return (
            <div>
                {this.renderWord()}
            </div>
        );
    }
}

export default WordList;
