import React, {useRef} from 'react';
import GetAllWord from "../../components/DataDB/DataDB";
import ModalWindow from "../addWord/modalWindow/modalWindow";

function ThemesList(props) {
    let words = useRef([]);
    function addThemeHandler() {
        GetAllWord().then(value => {
            words = value
        });
    }
    function getWords() {
        addThemeHandler();
        return words
    }
    return (
        <div className = 'body'>
            <button onClick = {addThemeHandler}>cheate new them</button>
            <ModalWindow words = {getWords()}/>
        </div>
    );
}

export default ThemesList;
