import React, {Component} from 'react';
import GetAllWord from "../../../components/DataDB/DataDB";
import Word from "../../wordList/Word/Word";
import './modalWindow.css';
class ModalWindow extends Component {
    state = {
        words: [],
        themesWords: []
    };
    componentDidMount() {
        GetAllWord().then(value => {
                this.setState({
                    words: value
                })
            }
        )
    }
    addWordsHandler = event =>{
        let themesWordsBlock = document.querySelectorAll('.modal-string.active-string');
        themesWordsBlock.forEach(item => {
            let word = {'word':item.firstChild.textContent, 'translation': item.lastChild.textContent};
            let themesWords = this.state.themesWords;
            themesWords.push(word);
            this.setState({
                themesWords
            });
        });
        console.log(this.state.themesWords)
    };
    onClickHandler = event =>{
        event.currentTarget.classList.toggle('active-string');
        if(event.target.className === 'modal-string active-string'){
            let word = {'word':event.currentTarget.firstChild.textContent, 'translation': event.currentTarget.lastChild.textContent};
            let themesWords = this.state.themesWords;
            themesWords.push(word);
            this.setState({
                themesWords
            });
        }else {
            let themesWords = this.state.themesWords;
            for (let i = 0; i < themesWords.length; i++){
                if(themesWords[i].word === this.state.themesWords[i].word ){
                    themesWords.splice(i, 1);
                    this.setState({
                        themesWords
                    });
                }
            }
        }
        console.log(this.state.themesWords)
    };
    renderModalWindow(){
        return this.state.words.map(item =>{
            return  <div key = {item.id} onClick = {event => event.currentTarget.classList.toggle('active-string')} className = 'modal-string' >
                <p>{item.word}</p>
                <p>{item.translation}</p>
            </div>
        })
    }

    render() {
        return (
            <div className = 'ModalWindow'>
                {this.renderModalWindow()}
                <button onClick={this.addWordsHandler}>add</button>
            </div>
        );
    }
}

export default ModalWindow;
