import React, {Component} from 'react';
import {GetAllThemesFromDB} from "../../components/DataDB/DataDB";
import './learnWords.css'
import ModalWindowChooseTheme from "./modalWindowChooseTheme/modalWindowChooseTheme";
class LearnWords extends Component {
    constructor(){
        super();
        this.state = {
            currentWord: {},
            currentNumber: 1,
            isRightWord: false,
            userWord: '',
            themes: [],
            currentTheme: [],
            isOpenWindow:false,
        };
        this.changeTheme = this.changeTheme.bind(this)
    }
    changeTheme = (value) =>{
        this.setState({
            currentTheme: value,
        });
        this.createWindow()
    };
    checkWordHandler = () => {
       if(this.state.userWord === this.state.currentWord.word){
           this.setState({
               isRightWord:true,
               currentTheme: this.state.currentTheme.list.splice(this.state.currentWord, 1)
           })
       }else {
           this.setState({
               isRightWord:false
           })
       }
    };
    changeWordHandler = () => {
        this.setState({
            currentWord: this.state.currentTheme.list[this.state.currentNumber].word,
            currentNumber: Math.floor(Math.random() * this.state.currentTheme.list.length)
        });
        console.log( this.state)
    };
    createWindow = () => {
        this.setState({
            isOpenWindow: !this.state.isOpenWindow
        });
        console.log( this.state)
    };
    componentDidMount() {
        GetAllThemesFromDB().then(value => {
            this.setState({
                themes: value,
            })
        });
    }

    render() {
        return (
            <div className = 'body'>
                <button onClick={this.createWindow}>Выбрать тему</button>
                {this.state.isOpenWindow ?(
                    <div>
                        <p>{this.state.currentWord.transaction}</p>
                        <button onClick={this.changeWordHandler}>Change</button>
                        <input onChange={event => {this.setState({userWord:event.target.value})}} type="text"/>
                        <button onClick={this.checkWordHandler}>Check</button>
                        <p>{this.state.isRightWord ? 'верно':'неверно'}</p>
                    </div>)
                    :(<ModalWindowChooseTheme themes={this.state.themes} changeTheme = {this.changeTheme}/>)
                }
            </div>
        );
    }
}

export default LearnWords;
