import React, {useState} from 'react';
import {GetAllThemesFromDB, GetAllWordsFromDB} from "../../components/DataDB/DataDB";
import ModalWindowThemes from "./modalWindowThemes/modalWindowThemes";

function ThemesList() {
    let [words, setWords] = useState([]);
    let [themes, setTemes] = useRef([]);
     async function addThemeHandler() {
         let result = await GetAllWordsFromDB();
         setWords(prevState =>{
             return {...prevState, ...result}
         });

     }
    async function getThemes() {
         let data = await GetAllThemesFromDB();
         setTemes(prevState =>{
             return {...prevState, ...data}
         });
    }
    function renderThemes() {
        getThemes();
        console.log(themes)
    }
    return (
        <div className = 'body'>
            <button onClick = {addThemeHandler}>cheate new them</button>
            {!!(words[0]) ? <ModalWindowThemes />: <h1>123</h1>}

        </div>
    );
}

export default ThemesList;
